// src/redux/store.js
import { createStore, applyMiddleware, compose } from 'redux';
import thunk from 'redux-thunk'; // Importing thunk directly

import rootReducer from './reducer'; // Import your combined reducers

// Use the compose function to apply middleware
const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;

// Apply middleware (in this example, using redux-thunk)
const store = createStore(
  rootReducer,
  composeEnhancers(applyMiddleware(thunk))
);

export default store;
