import React from "react";
import { useDispatch, useSelector } from 'react-redux';
import { addTodo, toggleTodo, removeTodo } from '../redux/actions';
import { TodoList } from '../Components/TodoList';
import { ITodo } from '../Components/interfaces';
import { ToDoForm } from "../Components/ToDoForm";

declare var confirm : (question: string) => boolean
export const TodosPage: React.FC = () => {
  const dispatch = useDispatch();
  const todosRedux: ITodo[] = useSelector((state: { todos: ITodo[] }) => state.todos);

  const addHandler = (title: string) => {
    // Dispatch the addTodo action
    dispatch(addTodo(title));
  }

  const toggleHandler = (id: number) => {
    // Dispatch the toggleTodo action
    dispatch(toggleTodo(id));
  }

  const removeHandler = (id: number) => {
    const shouldRemove = confirm('Are you sure?');
    if (shouldRemove) {
      // Dispatch the removeTodo action
      dispatch(removeTodo(id));
    }
  }
    return (
     <React.Fragment>
        <ToDoForm onAdd={addHandler}/>
        <TodoList 
          todos={todosRedux}
          onToggle={toggleHandler}
          onRemove={removeHandler}
        />
    </React.Fragment>
    )
}
