// Assuming your initial state looks like this
const initialState: { todos: ITodo[] } = {
    todos: [],
};

// Assuming you have a root reducer
const rootReducer = (state = initialState, action: TodoActionTypes): { todos: ITodo[] } => {
    switch (action.type) {
        case ADD_TODO:
            return {
                ...state,
                todos: [...state.todos, { id: Date.now(), title: action.payload.title, completed: false }],
            };
        case TOGGLE_TODO:
            return {
                ...state,
                todos: state.todos.map(todo =>
                    todo.id === action.payload.id ? { ...todo, completed: !todo.completed } : todo
                ),
            };
        case REMOVE_TODO:
            return {
                ...state,
                todos: state.todos.filter(todo => todo.id !== action.payload.id),
            };
        default:
            return state;
    }
};

export default rootReducer;
