// Components/TodosPage.tsx
import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ITodo } from '../Components/interfaces';
import { addTodo, toggleTodo, removeTodo } from '../redux/reducers/todos';
import { TodoList } from '../Components/TodoList';
import { ToDoForm } from '../Components/ToDoForm';

declare var confirm: (question: string) => boolean;

export const TodosPage: React.FC = () => {
  const todosRedux: ITodo[] = useSelector((state: { todos: ITodo[] }) => state.todos);
  const dispatch = useDispatch();

  console.log('Todos in the Redux store:', todosRedux);

  useEffect(() => {
    // Fetch todos from API or local storage if needed
  }, []);

  const addHandler = (title: string) => {
    dispatch(addTodo(title));
  };

  const toggleHandler = (id: number) => {
    dispatch(toggleTodo(id));
  };

  const removeHandler = (id: number) => {
    const shouldRemove = confirm('Are you sure?');
    if (shouldRemove) {
      dispatch(removeTodo(id));
    }
  };

  return (
      <React.Fragment>
        <ToDoForm onAdd={addHandler} />
        <TodoList todos={todosRedux} onToggle={toggleHandler} onRemove={removeHandler} />
      </React.Fragment>
  );
};
